import { Component, OnInit } from '@angular/core';
import { Country } from 'src/app/models/country';
import { ApiService } from 'src/app/services/api.service';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.scss']
})
export class MainComponent {

  constructor(private apiService: ApiService){}

  unis: any[];
  countries: Country[] = [{name: 'UK', value: 'UNITED+KINGDOM'}, {name: 'US', value: 'UNITED+STATES'}];
  country: Country = this.countries[0];

  ngOnInit(): void {
    this.getData();
  }

  onChange(value:any): void { 
    this.country = value;
    console.log(this.country);
    this.getData();
  }

  getData(): any {
    this.apiService.getData(this.country.value).subscribe(data => {
      this.unis = data.sort(((a,b) => a.name.localeCompare(b.name)));
      console.log(this.country.value);
    }, error => {
      console.log(error);
    })
  }


}
<div id="wrapper">
    <div id="header">
      <img src="assets/imgs/university.jpg" width="800" />
    </div>
    <div id="content">
      <h1>List of {{country.name}} Universities</h1>

      <label>Choose a country</label>

      <select #mySelect name="countries" (change)="onChange(mySelect.value)">
        <option [value]="this.countries[0]">{{this.countries[0].name}}</option>
        <option [value]="this.countries[1]">{{this.countries[1].name}}</option>
      </select>
      <!-- replace #root -->
      <app-unis [country]="this.country" [unis]="this.unis"></app-unis>
    </div>
    <div id="footer">
      <p>&copy; 2023 UK Universities</p>
    </div>
</div>
