import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.scss']
})
export class MainComponent {

  country: string;
  countries: string[] = ["UNITED+KINGDOM", "UNITED+STATES"];

  ngOnInit( ): void {
    console.log("HI");
  }

  getCountry(value:any): void { 
    this.country = value;
    console.log(this.country);
  }


}
<div id="wrapper">
    <div id="header">
      <img src="assets/imgs/university.jpg" width="800" />
    </div>
    <div id="content">
      <h1>List of UK Universities</h1>

      <label for="countries">Choose a country</label>

      <select name="countries" onchange="getCountry(this.value)">
        <option value="{{this.countries[0]}}">{{this.countries[0]}}</option>
        <option value="{{this.countries[1]}}">{{this.countries[1]}}</option>
      </select>
      <!-- replace #root -->
      <app-unis [country]="this.country"></app-unis>
    </div>
    <div id="footer">
      <p>&copy; 2023 UK Universities</p>
    </div>
</div>
